import React from "react";
import AuthorizationWin from "./components/AuthorizationWin";
import PersonalArea from "./components/PersonalArea";
import HomePage from './components/HomePage';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import './App.css'; // Импортируйте CSS файл для стилей

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            background: '#000000', // Стиль по умолчанию
        };
    }

    handleRouteChange = (path) => {
        if (path === '/AuthorizationWin') {
            this.setState({ background: '#000000' });
        } else {
            this.setState({ background: '#000000' });
        }
    }

    render() {
        return (
            <BrowserRouter>
                <div className={this.state.background}>
                    <Routes>
                        <Route
                            path="/"
                            element={<HomePage onRouteChange={() => this.handleRouteChange('/')} />}
                        />
                        <Route
                            path="/AuthorizationWin"
                            element={<AuthorizationWin onRouteChange={() => this.handleRouteChange('/AuthorizationWin')} />}
                        />
                        <Route
                            path="/PersonalArea"
                            element={<PersonalArea onRouteChange={() => this.handleRouteChange('/PersonalArea')} />}
                        />
                    </Routes>
                </div>
            </BrowserRouter>
        );
    }
}

export default App;
